<?php namespace App\Handlers\Events;use App\Events\MemberLoginEvent;use Maras\Contracts\LogsRepositoryInterface;use Auth;use Carbon\Carbon;class MemberLoginEventHandler{    protected $logsRepository;    /**     * Create the event handler.     * @param LogsRepository $logsRepository     * @return void     */    public function __construct(LogsRepositoryInterface $logsRepository)    {        $this->logsRepository = $logsRepository;    }    /**     * Handle the event.     *     * @param  MemberLoginEvent  $event     * @return void     */    public function handle(MemberLoginEvent $event)    {        // 執行登入後的記錄 這裏為了範例起見 先將它註解掉//        if (Auth::check())            $this->logsRepository->insertMemberLoginLogs(Auth::user(), $event->request->ip(), Carbon::NOW());    }}